import React, { useRef, useState } from 'react';

function VideoPlayer({ src }) {
    const videoRef = useRef(null);
    const [duration, setDuration] = useState(null);
    // phase 表示当前点击次数：0 表示还未播放；1 播放完第一段；2 播放完第二段；3 播放完全部
    const [phase, setPhase] = useState(0);
    const [targetTime, setTargetTime] = useState(0);

    // 获取视频总时长
    const handleLoadedMetadata = () => {
        if (videoRef.current) {
            setDuration(videoRef.current.duration);
        }
    };

    // 点击按钮控制播放区间
    const handleButtonClick = () => {
        if (!duration) return;
        let newPhase = phase + 1;
        let newTargetTime = 0;
        if (newPhase === 1) {
            // 第一次点击：播放前1/4
            newTargetTime = duration / 4;
        } else if (newPhase === 2) {
            // 第二次点击：播放下一个1/4，即前半部分
            newTargetTime = duration / 2;
        } else if (newPhase === 3) {
            // 第三次点击：播放剩余部分直到结束
            newTargetTime = duration;
        } else {
            // 播放完毕后不再操作
            return;
        }
        setPhase(newPhase);
        setTargetTime(newTargetTime);
        videoRef.current.play();
    };

    // 监听视频播放时间，一旦达到目标时间就暂停
    const handleTimeUpdate = () => {
        if (videoRef.current && videoRef.current.currentTime >= targetTime) {
            videoRef.current.pause();
        }
    };

    // 禁用点击视频的手动播放
    const handleVideoClick = (e) => {
        e.preventDefault();
    };

    return (
        <div style={{
            borderRadius: '8px',
            boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
            padding: '10px',
            margin: '10px',
            width: '340px',
            textAlign: 'center'
        }}>
            <video
                ref={videoRef}
                src={src}
                onLoadedMetadata={handleLoadedMetadata}
                onTimeUpdate={handleTimeUpdate}
                onClick={handleVideoClick}
                width="320"
                height="240"
                style={{ borderRadius: '8px' }}
            />
            <br />
            <button
                onClick={handleButtonClick}
                style={{
                    marginTop: '10px',
                    padding: '8px 16px',
                    border: 'none',
                    borderRadius: '4px',
                    background: '#007bff',
                    color: '#fff',
                    cursor: 'pointer'
                }}
            >
                {phase < 2 ? "播放下1/4" : "播放剩余部分"}
            </button>
        </div>
    );
}

function App() {
    return (
        <div style={{ display: 'flex', justifyContent: 'center' }}>
            <VideoPlayer src="1.mp4" />
            <VideoPlayer src="1.mp4" />
            <VideoPlayer src="1.mp4" />
        </div>
    );
}

export default App;
