import React, { useRef, useState } from 'react';

function VideoPlayer({ src }) {
    const videoRef = useRef(null);
    const [duration, setDuration] = useState(null);
    const [quarterIndex, setQuarterIndex] = useState(0);
    const [targetTime, setTargetTime] = useState(0);

    // 获取视频总时长
    const handleLoadedMetadata = () => {
        if (videoRef.current) {
            setDuration(videoRef.current.duration);
        }
    };

    // 点击按钮播放下一个 1/4 的区间
    const handleButtonClick = () => {
        if (!duration) return; // 视频时长未知则不操作
        if (quarterIndex >= 4) return; // 全部区间已播放完
        const newQuarterIndex = quarterIndex + 1;
        const newTargetTime = (duration * newQuarterIndex) / 4;
        setQuarterIndex(newQuarterIndex);
        setTargetTime(newTargetTime);
        videoRef.current.play();
    };

    // 当播放进度达到目标时间时暂停
    const handleTimeUpdate = () => {
        if (videoRef.current && videoRef.current.currentTime >= targetTime) {
            videoRef.current.pause();
        }
    };

    // 禁用点击视频的手动播放
    const handleVideoClick = (e) => {
        e.preventDefault();
    };

    return (
        <div style={{ margin: '10px' }}>
            <video
                ref={videoRef}
                src={src}
                onLoadedMetadata={handleLoadedMetadata}
                onTimeUpdate={handleTimeUpdate}
                onClick={handleVideoClick}
                width="320"
                height="240"
            />
            <br />
            <button onClick={handleButtonClick}>播放下1/4</button>
        </div>
    );
}

function App() {
    return (
        <div style={{ display: 'flex', justifyContent: 'center' }}>
            <VideoPlayer src="1.mp4" />
            <VideoPlayer src="1.mp4" />
            <VideoPlayer src="1.mp4" />
        </div>
    );
}

export default App;
